package day22;

public class WarmupFoorloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// print nums from 1 to 10 inclusive using for loop in one line

		for (int i = 1; i <= 10; i++) {
			System.out.print(i);

		}

//	print nums from 10 to 1 inclusive using for loop in one line
		System.out.println();
		for (int i = 10; i >= 1; i--) {
			System.out.print(i);

		}
	}
}
